mapper.update:
UPDATE t_cate_main
   SET
	    usediv = #{useDiv}
	   ,modifyDate = now()
	   ,modifyUser = #{modifyUser}
 WHERE
	   maincode = #{mainCode}

mapper.select:
		SELECT a.maincode, a.mainname, b.subcode, b.subname, b.subvalue, b.subvalue2, b.subvalue3, b.vieworder,
		       b.usediv, b.etcdesc,to_char(b.createDate, 'YYYY-MM-DD') as createDay, b.deleteFlag
		  FROM t_cate_main a, t_cate_sub b
		 WHERE a.mainCode = b.mainCode
		   AND a.mainCode = #{mainCode}
		   AND b.subCode = #{subCode}
